import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { MaterialCommunityIcons } from '@expo/vector-icons';

import HomeScreen from './Screens/HomeScreen';
import MusicScreen from './Screens/MusicScreen';
import ProfileScreen from './Screens/ProfileScreen';

const homeName = 'Home';
const musicName = 'Music';
const profileName = 'Profile';

const Tab = createBottomTabNavigator();

function MainContainer() {
    return(
        <NavigationContainer>
            <Tab.Navigator
              initalRouteName={homeName}
              screenOption={({ route }) => ({
                tabBarIcon: ({ focusd, color, size }) => {
                    let iconName;
                    let rn = route.name;
                    let iconSize = focused ? 30 : 25;
                    let iconStyle = { marginBottom: -11};

                    if (rn === homeName) iconName = 'home';
                    else if (rn === musicName) iconName ='music';
                    else if (rn === profileName) iconName = 'account';

                    return <MaterialCommunityIcons name={iconName} size={size} color={color} />;
                },
            })}
            screenOptions={{
                activeTintColor: 'tomato',
                inactiveTintColor: 'grey',
                labelStyle: { paddingBottom: 10, fontSize: 10 },
                style: { padding: 10, height: 70}
              }}>

                <Tab.Screen name={homeName} component={HomeScreen} />
                <Tab.Screen name={musicName} component={MusicScreen} />
                <Tab.Screen name={profileName} component={ProfileScreen} />
            </Tab.Navigator>
        </NavigationContainer>
    );
}

export default MainContainer;
